body.script:
// This line is now completely redundant and can be removed. Plugins are registered globally above.
// gsap.registerPlugin(InertiaPlugin);

function initGlowingInteractiveDotsGrid() {
  const colorPalettes = {
    // Added a 'default' palette for fallback cases
    'default': { base: "#383a3f", active: "#C080FF" }, // Using your 'purple' as the default
    'purple': { base: "#383a3f", active: "#C080FF" },
    'teal':    { base: "#383a3f", active: "#40EFDE" },
     'teal-light':    { base: "#ffffff", active: "#40EFDE" },
        'purple-light': { base: "#ffffff", active: "#C080FF" },
    // Add more custom palettes here!
  };

  document.querySelectorAll('[data-pixel-dots]').forEach(container => {
    const paletteName = container.getAttribute('data-pixel-dots');
    // This line will now correctly fall back to 'default' if paletteName is not found or empty
    const colors = colorPalettes[paletteName] || colorPalettes['default'];

    const threshold      = 200;
    const speedThreshold = 100;
    const shockRadius    = 325;
    const shockPower     = 3;
    const maxSpeed       = 5000;

    let dots       = [];
    let dotCenters = [];

    function buildGrid() {
      container.innerHTML = "";
      dots = [];
      dotCenters = [];

      const style = getComputedStyle(container);
      const dotPx = parseFloat(style.fontSize);
      const gapPx = dotPx * 2;
      const contW = container.clientWidth;
      const contH = container.clientHeight;

      const cols  = Math.floor((contW + gapPx) / (dotPx + gapPx));
      const rows  = Math.floor((contH + gapPx) / (dotPx + gapPx));
      const total = cols * rows;

      for (let i = 0; i < total; i++) {
        const d = document.createElement("div");
        d.classList.add("dot");
        gsap.set(d, { x: 0, y: 0, backgroundColor: colors.base });
        d._inertiaApplied = false;

        container.appendChild(d);
        dots.push(d);
      }

      requestAnimationFrame(() => {
        dotCenters = dots.map(d => {
          const r = d.getBoundingClientRect();
          return {
            el: d,
            x:  r.left + window.scrollX + r.width  / 2,
            y:  r.top  + window.scrollY + r.height / 2
          };
        });
      });
    }

    window.addEventListener("resize", buildGrid);
    buildGrid();

    let lastTime = 0, lastX = 0, lastY = 0;

    window.addEventListener("mousemove", e => {
      const now   = performance.now();
      const dt    = now - lastTime || 16;
      let   dx    = e.pageX - lastX;
      let   dy    = e.pageY - lastY;
      let   vx    = dx / dt * 1000;
      let   vy    = dy / dt * 1000;
      let   speed = Math.hypot(vx, vy);

      if (speed > maxSpeed) {
        const scale = maxSpeed / speed;
        vx *= scale; vy *= scale; speed = maxSpeed;
      }

      lastTime = now;
      lastX    = e.pageX;
      lastY    = e.pageY;

      requestAnimationFrame(() => {
        dotCenters.forEach(({ el, x, y }) => {
          const dist = Math.hypot(x - e.pageX, y - e.pageY);
          const t    = Math.max(0, 1 - dist / threshold);
          const col  = gsap.utils.interpolate(colors.base, colors.active, t);
          gsap.set(el, { backgroundColor: col });

          if (speed > speedThreshold && dist < threshold && !el._inertiaApplied) {
            el._inertiaApplied = true;
            const pushX = (x - e.pageX) + vx * 0.005;
            const pushY = (y - e.pageY) + vy * 0.005;

            gsap.to(el, {
              inertia: { x: pushX, y: pushY, resistance: 750 },
              onComplete() {
                gsap.to(el, {
                  x: 0,
                  y: 0,
                  duration: 1.5,
                  ease: "elastic.out(1,0.75)"
                });
                el._inertiaApplied = false;
              }
            });
          }
        });
      });
    });

    window.addEventListener("click", e => {
      dotCenters.forEach(({ el, x, y }) => {
        const dist = Math.hypot(x - e.pageX, y - e.pageY);
        if (dist < shockRadius && !el._inertiaApplied) {
          el._inertiaApplied = true;
          const falloff = Math.max(0, 1 - dist / shockRadius);
          const pushX    = (x - e.pageX) * shockPower * falloff;
          const pushY    = (y - e.pageY) * shockPower * falloff;

          gsap.to(el, {
            inertia: { x: pushX, y: pushY, resistance: 750 },
            onComplete() {
              gsap.to(el, {
                x: 0,
                y: 0,
                duration: 1.5,
                ease: "elastic.out(1,0.75)"
              });
              el._inertiaApplied = false;
            }
          });
        }
      });
    });
  });
}

document.addEventListener('DOMContentLoaded', function() {
  initGlowingInteractiveDotsGrid();
});

const button = document.querySelector('.pixel-button');
const overlay = button.querySelector('.pixel-overlay');

const pixelSize = 6;
let grid = { rows: 0, cols: 0 };

function setupPixelGrid() {
  if (!overlay || !button) return;
  overlay.innerHTML = '';
  const w = button.offsetWidth;
  const h = button.offsetHeight;
  const cols = Math.ceil(w / pixelSize);
  const rows = Math.ceil(h / pixelSize);
  overlay.style.gridTemplateColumns = `repeat(${cols}, ${pixelSize}px)`;
  overlay.style.gridTemplateRows = `repeat(${rows}, ${pixelSize}px)`;
  for (let y = 0; y < rows; y++) {
    for (let x = 0; x < cols; x++) {
      const p = document.createElement('div');
      p.classList.add('pixel');
      p.style.width = p.style.height = `${pixelSize}px`;
      p.dataset.row = y;
      p.dataset.col = x;
      overlay.appendChild(p);
    }
  }
  grid = { rows, cols };
}

function animateRadialFill(visible, fromX, fromY) {
  const pixels = overlay.querySelectorAll('.pixel');
  pixels.forEach(p => {
    const row = +p.dataset.row;
    const col = +p.dataset.col;
    const dist = Math.hypot(row - fromY, col - fromX);
    const jitter = gsap.utils.random(0, 0.2);
    gsap.to(p, {
      opacity: visible ? 1 : 0,
      duration: 0.5,
      delay: dist * 0.03 + jitter,
      ease: "power4.out"
    });
  });
}

document.addEventListener("DOMContentLoaded", () => {
  if (button && overlay) {
    setupPixelGrid();
    window.addEventListener('resize', setupPixelGrid);

    button.addEventListener('mouseenter', e => {
      const rect = button.getBoundingClientRect();
      const col = Math.floor((e.clientX - rect.left) / pixelSize);
      const row = Math.floor((e.clientY - rect.top) / pixelSize);
      animateRadialFill(true, col, row);
    });

    button.addEventListener('mouseleave', () => {
      animateRadialFill(false, Math.floor(grid.cols / 2), Math.floor(grid.rows / 2));
    });
  }
});

document.addEventListener('DOMContentLoaded', function() {
    // 1. Select the container that holds the data-video-url and is your Webflow HTML Embed.
    //    Based on your description, this is the element with classes 'video-player-embed'
    //    and the 'data-video-url' attribute.
    const videoContainers = document.querySelectorAll('.video-player-embed[data-video-url]');

    // Loop through each instance of this component on the page
    videoContainers.forEach(container => {
        let videoUrl = container.getAttribute('data-video-url');

        if (videoUrl) {
            // --- Optional: URL conversion logic (keep if you want to accept watch URLs) ---
            // For YouTube 'watch' URLs (e.g., https://www.youtube.com/watch?v=VIDEO_ID)
            if (videoUrl.includes('youtube.com/watch')) {
                try {
                    const urlParams = new URLSearchParams(new URL(videoUrl).search);
                    const videoId = urlParams.get('v');
                    if (videoId) {
                        videoUrl = `https://www.youtube.com/embed/${videoId}`;
                        // For enhanced privacy, use: videoUrl = `https://www.youtube-nocookie.com/embed/${videoId}`;
                    }
                } catch (e) {
                    console.error("Error parsing YouTube watch URL:", e);
                }
            }
            // For YouTube 'youtu.be' short URLs (e.g., https://youtu.be/VIDEO_ID)
            else if (videoUrl.includes('youtu.be/')) {
                try {
                    // Extract video ID from youtu.be link, handling potential query parameters or hashtags
                    const videoId = videoUrl.split('youtu.be/')[1].split(/[?#]/)[0];
                    if (videoId) {
                        videoUrl = `https://www.youtube.com/embed/${videoId}`;
                    }
                } catch (e) {
                    console.error("Error parsing YouTube short URL:", e);
                }
            }
            // --- End of URL conversion logic ---

            // 2. Find the iframe within the current 'container'.
            //    The path is: container ('.video-player-embed')
            //    -> child '.video-wrapper'
            //    -> child '.video-iframe'
            const iframe = container.querySelector('.video-wrapper .video-iframe');

            if (iframe) {
                // If the iframe is found, set its src attribute
                iframe.src = videoUrl;
                console.log('Video URL set successfully:', videoUrl, 'for iframe:', iframe);
            } else {
                // If the iframe is NOT found, log a warning with the container's HTML for debugging
                console.warn('JavaScript Warning: Could not find .video-iframe inside the expected container.');
                console.log('Container that was searched:', container);
                console.log('HTML of the container (check for nested structure):', container.outerHTML);
            }
        } else {
            console.warn('JavaScript Warning: data-video-url attribute was empty for container:', container);
        }
    });
});

console.log("--- Custom Swiper Script Loading! ---");

// This array will hold references to each Swiper instance for debugging
window.mySwiperInstances = window.mySwiperInstances || [];

function numberWithZero(num) {
    return num < 10 ? "0" + num : num;
}

$(".section_case-study-slider").each(function (index) {
  const componentEl = $(this);
  const mainSwiperEl = componentEl.find(".swiper.is-slider-bg")[0];
  const swiperWrapperEl = componentEl.find(".swiper-wrapper.is-slider-bg")[0]; 

  let swiperInstance = null; // Still declared locally for initial assignment

  const initializeSwiper = () => {
    if (swiperInstance) {
      swiperInstance.destroy(true, true);
    }

    swiperInstance = new Swiper(mainSwiperEl, {
      slidesPerView: 1,
      speed: 600,
      loop: true,
      effect: "fade",
      allowTouchMove: false,
      keyboard: {
        enabled: true,
        onlyInViewport: true,
      },
      navigation: {
        nextEl: componentEl.find(".swiper-next")[0],
        prevEl: componentEl.find(".swiper-prev")[0]
      },
      observer: true,
      observeParents: true,
      observeSlideChildren: true
    });

    // --- NEW: Store the instance in a global array for debugging ---
    window.mySwiperInstances[index] = swiperInstance; 
    // You can now access the first slider's instance via window.mySwiperInstances[0] in console

let totalSlides = numberWithZero(swiperInstance.slides.filter(slide => !slide.classList.contains('swiper-slide-duplicate')).length);    componentEl.find(".swiper-number-total").text(totalSlides);

    swiperInstance.on("slideChange", function (e) {
      let slideNumber = numberWithZero(e.realIndex + 1);
      componentEl.find(".swiper-number-current").text(slideNumber);
      console.log(`Slide changed to real index: ${e.realIndex + 1}. Current slide number display: ${slideNumber}`);
    });

    console.log(`Swiper initialized within section: ${componentEl.attr('class')} with ${swiperInstance.slides.length} slides.`);
  };

  // --- Mutation Observer Logic ---
  if (swiperWrapperEl) {
    if (componentEl.find(".swiper-slide.is-slider-bg").length > 0) {
      initializeSwiper();
    } else {
      const observer = new MutationObserver((mutationsList, observer) => {
        if (componentEl.find(".swiper-slide.is-slider-bg").length > 0) {
          initializeSwiper();
          observer.disconnect();
          console.log(`MutationObserver: Slides detected for section ${componentEl.attr('class')}, Swiper initialized and observer disconnected.`);
        }
      });

      observer.observe(swiperWrapperEl, { childList: true, subtree: true });
      console.log(`MutationObserver: Started observing swiper-wrapper.is-slider-bg within section: ${componentEl.attr('class')} for CMS content.`);
    }
  } else {
    console.error(`Error: .swiper-wrapper.is-slider-bg not found within .section_case-study-slider (Current element: ${componentEl.attr('class')}). Ensure your Swiper HTML structure is correct.`);
  }
});
