div.script:
function adjustMainMargin() {
  const header = document.querySelector('.nav2');
  const main = document.querySelector('.main');
  const heroCopy = document.querySelector('[homepage="hero-copy"]');
  const sectionHero = document.querySelector('.section-hero');

  if (header && main) {
    const headerHeight = header.offsetHeight;

    // Set the negative margin on main
    main.style.marginTop = `-${headerHeight}px`;

    // Handle hero-copy top padding
    if (heroCopy) {
      // Store original padding on first run
      if (!heroCopy.dataset.originalPaddingTop) {
        const computedStyle = window.getComputedStyle(heroCopy);
        heroCopy.dataset.originalPaddingTop = computedStyle.paddingTop;
      }

      const originalPaddingTop = heroCopy.dataset.originalPaddingTop;

      if (originalPaddingTop && originalPaddingTop !== '0px') {
        heroCopy.style.paddingTop = `calc(${originalPaddingTop} + ${headerHeight}px)`;
      } else {
        heroCopy.style.paddingTop = `${headerHeight}px`;
      }
    }

    // Handle section-hero styles
    if (sectionHero) {
      const computedStyle = window.getComputedStyle(sectionHero);

      // Store original bottom padding on first run
      if (!sectionHero.dataset.originalPaddingBottom) {
        sectionHero.dataset.originalPaddingBottom = computedStyle.paddingBottom;
      }

      // Store original max-height on first run
      if (!sectionHero.dataset.originalMaxHeight) {
        sectionHero.dataset.originalMaxHeight = computedStyle.maxHeight;
      }

      // Store original height on first run
      if (!sectionHero.dataset.originalHeight) {
        sectionHero.dataset.originalHeight = computedStyle.height;
      }

      // Apply bottom padding
      const originalPaddingBottom = sectionHero.dataset.originalPaddingBottom;
      if (originalPaddingBottom && originalPaddingBottom !== '0px') {
        sectionHero.style.paddingBottom = `calc(${originalPaddingBottom} + ${headerHeight}px)`;
      } else {
        sectionHero.style.paddingBottom = `${headerHeight}px`;
      }

      // Apply max-height
      const originalMaxHeight = sectionHero.dataset.originalMaxHeight;
      if (originalMaxHeight && originalMaxHeight !== 'none' && originalMaxHeight !== '0px') {
        sectionHero.style.maxHeight = `calc(${originalMaxHeight} + ${headerHeight}px)`;
      } else if (originalMaxHeight === '0px') {
        sectionHero.style.maxHeight = `${headerHeight}px`;
      }

      // Apply height
      const originalHeight = sectionHero.dataset.originalHeight;
      if (originalHeight && originalHeight !== 'auto' && originalHeight !== '0px') {
        sectionHero.style.height = `calc(${originalHeight} + ${headerHeight}px)`;
      } else if (originalHeight === '0px') {
        sectionHero.style.height = `${headerHeight}px`;
      }
    }

    console.log(`Header height: ${headerHeight}px applied to all elements`);
  }
}

// Function to handle header background on scroll
function handleHeaderBackground() {
  const header = document.querySelector('.nav2');

  if (header) {
    // Add transition style if not already added
    if (!header.dataset.transitionAdded) {
      header.style.transition = 'background-color 0.25s ease-in-out';
      header.dataset.transitionAdded = 'true';
    }

    // Check scroll position
    const scrollPosition = window.pageYOffset || document.documentElement.scrollTop;

    if (scrollPosition >= 70) {
      header.style.backgroundColor = 'white';
    } else {
      header.style.backgroundColor = 'transparent';
    }
  }
}

// Run on initial load
document.addEventListener('DOMContentLoaded', () => {
  adjustMainMargin();
  handleHeaderBackground(); // Check initial scroll position
});

// Run on window resize
window.addEventListener('resize', adjustMainMargin);

// Run on scroll
window.addEventListener('scroll', handleHeaderBackground);

// Optional: ResizeObserver for detecting header size changes
const header = document.querySelector('.nav2');
if (header && window.ResizeObserver) {
  const resizeObserver = new ResizeObserver(entries => {
    adjustMainMargin();
  });

  resizeObserver.observe(header);
}
